    # return location
    # \xe4\xb8\x8b\xe9\x9d\xa2\xe7\x94\x9f\xe6\x88\x90\xe9\x80\x9f\xe5\xba\xa6\xe5\x88\x9d\xe5\xa7\x8b\xe5\x8c\x96\xe4\xbf\xa1\xe6\x81\xaf,\xe4\xbd\x8d\xe7\xbd\xae\xe6\x9b\xb4\xe6\x96\xb0\xe7\x9a\x84\xe6\x97\xb6\xe5\x80\x99\xe5\xa6\x82\xe6\x9e\x9c\xe8\xb6\x8a\xe7\x95\x8c\xe4\xba\x86\xef\xbc\x8c\xe6\xb3\xa8\xe6\x84\x8f\xe5\xbe\x80\xe5\x9b\x9e\xe6\x8b\x89
    speed = np.array([0.1]*num);
    # \xe4\xb8\x8b\xe9\x9d\xa2\xe8\xae\xa1\xe7\xae\x97\xe5\x88\x9d\xe5\xa7\x8b\xe7\x9a\x84\xe5\xb1\x80\xe9\x83\xa8\xe6\x9c\x80\xe4\xbc\x98\xe5\x92\x8c\xe5\x85\xa8\xe5\xb1\x80\xe6\x9c\x80\xe4\xbc\x98;
    localBestY =  [shiyingdu(x) for x in location];
    localBestX = location;
    wholeBestY = max(localBestY);
    wholeBestX = location[localBestY.index(wholeBestY)];
