def initial_center(DataSet,num_of_center):
    dims = np.shape(DataSet)[1] # \xe8\x8e\xb7\xe5\xbe\x97\xe6\x95\xb0\xe6\x8d\xae\xe7\x9a\x84\xe7\xbb\xb4\xe6\x95\xb0
    centers=np.mat(np.zeros((num_of_center,dims))) # \xe5\x8f\x91\xe7\x8e\xb0python\xe4\xb8\x8ematlab\xe7\x9c\x9f\xe7\x9a\x84\xe5\xa5\xbd\xe5\x83\x8f\xe5\x95\x8a;

    # \xe5\xaf\xb9\xe4\xba\x8e\xe6\xaf\x8f\xe4\xb8\xaa\xe7\xbb\xb4\xe5\xba\xa6\xe6\xb1\x82\xe6\x95\xb0\xe6\x8d\xae\xe9\x9b\x86\xe7\x9a\x84\xe6\x9c\x80\xe5\xa4\xa7\xe5\x80\xbc\xe5\x92\x8c\xe6\x9c\x80\xe5\xb0\x8f\xe5\x80\xbc,\xe7\x84\xb6\xe5\x90\x8e\xe9\x9a\x8f\xe6\x9c\xba\xe8\xae\xa1\xe7\xae\x97\xe5\x90\x84\xe4\xb8\xaa\xe4\xb8\xad\xe5\xbf\x83\xe7\x82\xb9\xe7\x9a\x84\xe8\xaf\xa5\xe7\xbb\xb4\xe5\xba\xa6\xe7\x9a\x84\xe5\x80\xbc;
    for i in range(0,dims):
        minJ = min(DataSet[:,i])      # \xe5\x9c\xa8\xe4\xba\x8c\xe7\xbb\xb4\xe6\x95\xb0\xe7\xbb\x84\xe4\xb8\xad\xe8\x8e\xb7\xe5\xbe\x97\xe6\x9f\x90\xe4\xb8\x80\xe5\x88\x97\xe7\x9a\x84\xe6\x96\xb9\xe6\xb3\x95
        rangeJ = float(max(DataSet[:,i])-minJ) # \xe6\xb1\x82\xe5\x8f\x98\xe5\x8c\x96\xe8\x8c\x83\xe5\x9b\xb4,\xe8\xbf\x99\xe6\xa0\xb7\xe7\x94\x9f\xe6\x88\x90\xe9\x9a\x8f\xe6\x9c\xba\xe6\x95\xb0\xe8\xbf\x98\xe5\x8f\xaf\xe4\xbb\xa5\xe9\x80\x89\xe6\x88\x91\xe4\xbb\xac\xe4\xbd\xbf\xe7\x94\xa8\xe7\x9a\x84\xe6\x95\xb4\xe6\x95\xb0;
        centers[:,i]=minJ+rangeJ*np.random.rand(num_of_center,1) # \xe8\xbf\x99\xe9\x87\x8c\xe4\xbd\xbf\xe7\x94\xa8\xe7\x9a\x84\xe6\x98\xafnp\xe5\xba\x93\xe9\x87\x8c\xe9\x9d\xa2\xe7\x9a\x84random\xe6\x96\xb9\xe6\xb3\x95\xef\xbc\x8c\xe7\x9c\x8b\xe8\xb5\xb7\xe6\x9d\xa5\xe6\x9b\xb4\xe5\xbf\xab\xe4\xb8\x80\xe7\x82\xb9


