def kMeans(dataSet,num_of_centers):
    # \xe5\x85\x88\xe8\xbf\x9b\xe8\xa1\x8c\xe5\x88\x9d\xe5\xa7\x8b\xe5\x8c\x96\xe4\xb8\xad\xe5\xbf\x83\xe7\x82\xb9\xe5\x92\x8c\xe6\xaf\x8f\xe4\xb8\xaa\xe7\x82\xb9\xe7\x9a\x84\xe5\xbd\x92\xe5\xb1\x9e\xe7\xb0\x87
