        # \xe6\x9b\xb4\xe6\x96\xb0\xe4\xb8\xad\xe5\xbf\x83\xe7\x82\xb9\xe5\x9d\x90\xe6\xa0\x87
        for i in range(num_of_centers):
            point_in_clusteri = dataSet[ np.nonzero(cluster_of_point[:,0].A==i)[0] ] # \xe7\xbb\x9f\xe8\xae\xa1\xe5\xbd\x92\xe5\xb1\x9e\xe4\xb8\xbai\xe7\x9a\x84\xe6\x89\x80\xe6\x9c\x89\xe6\xb4\x97\xe6\xa0\x87\xe7\xbb\x84\xe6\x88\x90\xe7\x9a\x84
            cluster_centers[i,:] = np.mean(point_in_clusteri,axis=0);

    return cluster_centers,cluster_of_point



